function isWebp() {
	var webP = new Image();
	webP.onload = webP.onerror = () => {
		var support = webP.height == 2;
		var className = support ? "webp" : "no-webp";
		document.querySelector("body").classList.add(className);
	};
	webP.src = "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA";
}

function setFileSelectorHandler() {
	let fileUpload = document.querySelector("#file-upload");
	fileUpload.addEventListener("change", event => {
		let files = Array.from(fileUpload.files);
		let filesToUpload = document.querySelector("#files-to-upload");
		filesToUpload.innerHTML = "";
		files.forEach(file => {
			var li = document.createElement("li");
			li.classList.add("form__file-name");
			li.textContent = file.name;
			filesToUpload.appendChild(li);
		})
	});
}

isWebp();
setFileSelectorHandler();